# Create simple icon files using base64 encoded minimal PNG data

# Minimal 16x16 blue PNG (base64 encoded)
$icon16Data = ""

# Create directories if they don't exist
if (!(Test-Path "popup\dist\icons")) {
    New-Item -ItemType Directory -Path "popup\dist\icons" -Force
}

# Create minimal PNG files by writing simple binary data
function Create-MinimalPNG {
    param($size, $path)
    
    # Create a simple PNG header for a solid blue square
    $pngHeader = [byte[]](0x89, 0x50, 0x4E, 0x47, 0x0D, 0x0A, 0x1A, 0x0A)
    $ihdrChunk = [byte[]](0x00, 0x00, 0x00, 0x0D, 0x49, 0x48, 0x44, 0x52)
    $width = [BitConverter]::GetBytes([uint32]$size)
    $height = [BitConverter]::GetBytes([uint32]$size)
    if ([BitConverter]::IsLittleEndian) {
        [Array]::Reverse($width)
        [Array]::Reverse($height)
    }
    $ihdrData = $width + $height + [byte[]](0x08, 0x02, 0x00, 0x00, 0x00)
    $ihdrCrc = [byte[]](0x9A, 0x76, 0x82, 0x70)  # Pre-calculated CRC
    
    # Minimal IDAT chunk (compressed image data for solid blue)
    $idatChunk = [byte[]](0x00, 0x00, 0x00, 0x0C, 0x49, 0x44, 0x41, 0x54)
    $idatData = [byte[]](0x78, 0x9C, 0x63, 0x00, 0x00, 0xFF, 0x00, 0x00, 0x00, 0x01, 0x00, 0x01)
    $idatCrc = [byte[]](0x35, 0xAF, 0x06, 0x1E)  # Pre-calculated CRC
    
    # IEND chunk
    $iendChunk = [byte[]](0x00, 0x00, 0x00, 0x00, 0x49, 0x45, 0x4E, 0x44, 0xAE, 0x42, 0x60, 0x82)
    
    # Combine all chunks
    $pngData = $pngHeader + $ihdrChunk + $ihdrData + $ihdrCrc + $idatChunk + $idatData + $idatCrc + $iendChunk
    
    # Write to file
    [System.IO.File]::WriteAllBytes($path, $pngData)
}

# Create icons
try {
    Create-MinimalPNG 16 "popup\dist\icons\icon16.png"
    Create-MinimalPNG 48 "popup\dist\icons\icon48.png"
    Create-MinimalPNG 128 "popup\dist\icons\icon128.png"
    Write-Host "Simple PNG icons created successfully!"
} catch {
    Write-Host "Error creating icons: $($_.Exception.Message)"
}